import React, { Component } from 'react';
import { db } from '../services/firebase';

class ArrayTest extends Component {

    state = {
        peopledumbbellpress: null
    }

    componentDidMount(){
        console.log('mounted');
        db.collection('dumbell-press')
        .get()
        .then( snapshot => {
            const peopledumbbellpress = [];
            snapshot.forEach( doc => {
                const data = doc.data();
                peopledumbbellpress.push(data);
            })
            this.setState({peopledumbbellpress: peopledumbbellpress})
        })
        .catch( error => console.log(error))
    }

    render() {
        return (
            <div>
                <h1>firebase oefeningen</h1>
                {
                    this.state.peopledumbbellpress && 
                    this.state.peopledumbbellpress.map( person => {
                        return (
                            <div>
                                <p>{person.name} drukt {person.reps1}x {person.weight1}kg</p>
                            </div>
                        )
                    })
                }
            </div>
        )
    }
}

export default ArrayTest;
