import React, { Component } from 'react';
import { db } from '../services/firebase';

import { withStyles } from '@material-ui/core/styles';
import MuiAccordion from '@material-ui/core/Accordion';
import MuiAccordionSummary from '@material-ui/core/AccordionSummary';
import MuiAccordionDetails from '@material-ui/core/AccordionDetails';
import Typography from '@material-ui/core/Typography';
import closegripbenchfoto from '../images/close-grip-bench-press.jpg'; 

const Accordion = withStyles({
    root: {
        border: 'none',
        boxShadow: 'none',
        '&:not(:last-child)': {
        borderBottom: 'none',
        },
        '&:before': {
        display: 'none',
        },
        '&$expanded': {
        margin: 'auto',
        },
    },
    expanded: {},
})(MuiAccordion);

const AccordionSummary = withStyles({
    root: {
      backgroundColor: 'rgba(0, 0, 0, .03)',
      minHeight: 56,
      '&$expanded': {
        minHeight: 56,
      },
    },
    content: {
      '&$expanded': {
        margin: '12px 0',
      },
    },
    expanded: {},
})(MuiAccordionSummary);

const AccordionDetails = withStyles((theme) => ({
    root: {
      padding: theme.spacing(2),
    },
}))(MuiAccordionDetails);




const handleChange = (panel) => (event, newExpanded) => {
    const [expanded, setExpanded] = React.useState('');
    setExpanded(newExpanded ? panel : false);
  };


class ArrayTest extends Component {

    state = {
        exercisesArray: null,
        dumbbellpressPeopleArray: null,
    }

    componentDidMount(){
        db.collection('exercises').get().then( snapshot => {
            const exercisesArray = [];
            snapshot.forEach( doc => {
                const data = doc.data();
                exercisesArray.push(data);
            })
            this.setState({exercisesArray: exercisesArray})
        }).catch( error => console.log(error));
        db.collection('exercises').doc('dumbbell-press').collection('people').get().then( snapshot => {
            const dumbbellpressPeopleArray = [];
            snapshot.forEach( doc => {
                const data = doc.data();
                dumbbellpressPeopleArray.push(data);
            })
            this.setState({dumbbellpressPeopleArray: dumbbellpressPeopleArray})
        }).catch( error => console.log(error));
    }

    render() {
        return (
            <div style={{color: 'white'}}>
                <Accordion className="exerciseBlock" expanded={expanded === panel1} onChange={handleChange(data.id)} style={{border: 'none'}}>
                    <AccordionSummary aria-controls="panel1d-content" id="panel1d-header" style={{paddingLeft: '25px'}}>
                    <Typography>{data.exercisename}</Typography>
                    </AccordionSummary>
                    <AccordionDetails>
                        <Typography>
                        <img src={closegripbenchfoto} alt="not found" style={{width: '100%', height: 'auto'}}/><br></br>
                        Joooeeee
                        </Typography>
                    </AccordionDetails>
                </Accordion>
                <h1>firebase oefeningen</h1>
                {
                    this.state.exercisesArray && 
                    this.state.exercisesArray.map( exercise => {
                        return (
                            <div>
                                <p>{exercise.name}</p>
                            </div>
                        )
                    })
                }
                {
                    this.state.dumbbellpressPeopleArray && 
                    this.state.dumbbellpressPeopleArray.map( exercise => {
                        return (
                            <div>
                                <p>{exercise.name} drukt <br></br>{exercise.reps1}x {exercise.weight1}kg | {exercise.reps2}x {exercise.weight2}kg | {exercise.reps3}x {exercise.weight3}kg </p>
                            </div>
                        )
                    })
                }

            </div>
        )
    }
}

export default ArrayTest;
