import React , { Component } from 'react';

class Location extends Component {

    constructor(props){
        super(props);
        this.state = {
            latitude: null,
            longitude: null,
            userAddress: null,
            apikey: "AIzaSyAsjIFcS5UtxpAgk9VqboakwYPOuFXdh6U"
        };
        this.getLocation = this.getLocation.bind(this);
        this.getCoordinates = this.getCoordinates.bind(this);
    }

    getLocation(){
        if(navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(this.getCoordinates);
        } else {
            console.log('Geolocation is not supported by this browser.')
        }
    }

    getCoordinates(position){
        this.setState({
            latitude: position.coords.latitude,
            longitude: position.coords.longitude
        })
    }

    render(){

        return(
            <div style={{color: 'white'}}>
                <br></br>
                <button onClick={this.getLocation}>Get location</button>
                <p>Latitude: {this.state.latitude}</p>
                <p>Longitude: {this.state.longitude}</p>
                <p>Address: {this.state.userAddress}</p>
                {
                    <img src={`https://maps.googleapis.com/maps/api/staticmap?center=${this.state.latitude},${this.state.longitude}&zoom=14&size=400x300&sensor=false&markers=color:red%7C${this.state.latitude},${this.state.longitude}&key=${this.state.apikey}`} alt="" />
                }
            </div>
        )
    }
}

export default Location;
