import React, { Component } from 'react';
import { db } from '../services/firebase';

class ArrayTest extends Component {

    state = {
        exercisesArray: [],
        peopleArray: []
    }

    componentDidMount(){
        db.collection('exercises').get().then( snapshot => {
            const exercisesArray = [];
            snapshot.forEach( doc => {
                const data = doc.data();
                exercisesArray.push(data);
            })
            this.setState({exercisesArray: exercisesArray})
        });
        this.state.exercisesArray.forEach ( doc => {
            db.collection('exercises').doc(doc.idname).collection('people').get().then( snapshot => {
                const peopleArray = [];
                snapshot.forEach( doc => {
                    const data = doc.data();
                    peopleArray.push(data);
                })
                this.setState({peopleArray: peopleArray})
                console.log(peopleArray);
            }).catch( error => console.log(error));

        })

    }

    render() {
        return (
            <div style={{color: 'white'}}>
                <h1>firebase oefeningen</h1>
                {
                    this.state.exercisesArray && 
                    this.state.exercisesArray.map( exercise => {
                        return (
                            <div>
                                <p>{exercise.name}</p>
                            </div>
                        )
                    })
                }

            </div>
        )
    }
}

export default ArrayTest;
