import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import MuiAccordion from '@material-ui/core/Accordion';
import MuiAccordionSummary from '@material-ui/core/AccordionSummary';
import MuiAccordionDetails from '@material-ui/core/AccordionDetails';
import Typography from '@material-ui/core/Typography';

import data from '../data/data.json';

import closegripbenchfoto from '../images/close-grip-bench-press.jpg'; 
import { db } from '../services/firebase';

const Accordion = withStyles({
  root: {
    border: 'none',
    boxShadow: 'none',
    '&:not(:last-child)': {
      borderBottom: 'none',
    },
    '&:before': {
      display: 'none',
    },
    '&$expanded': {
      margin: 'auto',
    },
  },
  expanded: {},
})(MuiAccordion);

const AccordionSummary = withStyles({
  root: {
    backgroundColor: 'rgba(0, 0, 0, .03)',
    minHeight: 56,
    '&$expanded': {
      minHeight: 56,
    },
  },
  content: {
    '&$expanded': {
      margin: '12px 0',
    },
  },
  expanded: {},
})(MuiAccordionSummary);

const AccordionDetails = withStyles((theme) => ({
  root: {
    padding: theme.spacing(2),
  },
}))(MuiAccordionDetails);


const [expanded, setExpanded] = React.useState('');

  const handleChange = (panel) => (event, newExpanded) => {
    setExpanded(newExpanded ? panel : false);
  };

  var dumbbellpressPeopleArray = [];
  var exercisesArray = [];

  db.collection('exercises').get().then( snapshot => {
    snapshot.forEach( doc => {
      const data = doc.data();
      exercisesArray.push(data);
    })
  }).catch( error => console.log(error));
  db.collection('exercises').doc('dumbbell-press').collection('people').get().then( snapshot => {
    snapshot.forEach( doc => {
        const data = doc.data();
        dumbbellpressPeopleArray.push(data);
    })
  }).catch( error => console.log(error));

  console.log(exercisesArray);
  console.log(dumbbellpressPeopleArray);  

  const newData = data.map( (data) => {
    return (
        <div>
          <div>
            <Accordion className="exerciseBlock" expanded={expanded === data.id} onChange={handleChange(data.id)} style={{border: 'none'}}>
                <AccordionSummary aria-controls="panel1d-content" id="panel1d-header" style={{paddingLeft: '25px'}}>
                <Typography>{data.exercisename}</Typography>
                </AccordionSummary>
                <AccordionDetails>
                    <Typography>
                    <img src={closegripbenchfoto} alt="not found" style={{width: '100%', height: 'auto'}}/><br></br>
                    {data.people.map((people) => {
                      return (
                        <div><br></br>
                          <b>{people.name}</b> drukt <br></br>{people.weight}
                        </div>
                      )
                    })}
                    </Typography>
                </AccordionDetails>
            </Accordion>
          </div>    
        </div>

    )
  })

class NewListChest extends Component {

  state = {
    exercisesArray: null,
    dumbbellpressPeopleArray: null
}

  render() {

    return (
      <div>
          <Typography variant="h6" style={{color: 'white'}}>
              Chest exercises
          </Typography><br></br>

          {newData}

          {exercisesArray}



          <br></br>
          <br></br>
          <br></br>
      </div>
    )
  }
}

export default NewListChest;
