import React, { Component } from 'react';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import { db } from '../services/firebase';

import ArrowForwardIcon from '@material-ui/icons/ArrowForward';

class HomepageCard extends Component {

  state = {
    updatesArray: null,
  }

  componentDidMount() {
    db.collection('updates').get().then( snapshot => {
      const updatesArray = [];
      snapshot.forEach( doc => {
          const data = doc.data();
          console.log(data);
          updatesArray.push(data);
      })
      this.setState({updatesArray: updatesArray})
    }).catch( error => console.log(error));
  }

  render() {

    return (
      <div>
        <Card style={{width: '90%', backgroundColor: '#353766', color: 'white', borderRadius: '10px', marginLeft: '5%'}}>
        <CardActionArea>
            <CardContent>
            <Typography gutterBottom variant="h6" component="h2" align="center">
                Bicep curl dumbbell <br></br>
                10kg <ArrowForwardIcon fontSize="small" display="inline" /> 12kg
            </Typography>
            <Typography gutterBottom variant="subtitle2" component="h2" align="center">
                Martijn Creusen
            </Typography>
            </CardContent>
        </CardActionArea>
        </Card>

        <br></br>

        <Card style={{width: '90%', backgroundColor: '#353766', color: 'white', borderRadius: '10px', marginLeft: '5%'}}>
        <CardActionArea>
            <CardContent>
            <Typography gutterBottom variant="h6" component="h2" align="center">
                Incline dumbbell press <br></br>
                30kg <ArrowForwardIcon fontSize="small" display="inline" /> 32kg
            </Typography>
            <Typography gutterBottom variant="subtitle2" component="h2" align="center">
                Joep van Antwerpen
            </Typography>
            </CardContent>
        </CardActionArea>
        </Card>
    </div>
  );
  }


}

export default HomepageCard;
